import React from 'react';
import {
    Radar,
    RadarChart,
    PolarGrid,
    PolarAngleAxis,
    ResponsiveContainer,
  } from 'recharts';

/*
* Renders kinds transfomed used in Perfomance function
* @function TransformKind
* @param { String[] } tickItem
* @returns
*/
const TransformKind = (tickItem) => {
    const Kind = [
        'Cardio',
        'Energie',
        'Endurance',
        'Force',
        'Vitesse',
        'Intensité',
    ];
    if (tickItem)
    return Kind[tickItem - 1];
}

/*
 * Renders Performance Radar chart- SimpleRadarChart (Recharts)
 * @function
 * @param { Object } Performance
 * @param { String[] } Kind
 * @returns { JSX } Performance Radar chart
 */
const Performance = ({ performance }) => {
    return (
        <div className="anaItem performance">
      <ResponsiveContainer width="100%" height="100%">
        <RadarChart
          width={200}
          height={200}
          cx="50%"
          cy="50%"
          outerRadius="65%"
          data={performance.data}
        >
          <PolarGrid radialLines={false} />

          <PolarAngleAxis
            dataKey="kind"
            tickFormatter={TransformKind}
            stroke={`#fff`}
            dy={4}
            tickLine={false}
            style={{
              fontSize: '.7vw',
              fontWeight: '500',
            }}
          />

          <Radar
            name="Radar"
            dataKey="value"
            fill={`#ff0000`}
            fillOpacity={0.7}
          />
        </RadarChart>
      </ResponsiveContainer>
    </div>
    );
};

export default Performance;
