import React from 'react';
import {
    LineChart,
    Line,
    XAxis,
    YAxis,
    Tooltip,
    ResponsiveContainer,
  } from 'recharts';

/*
* * Renders the tooltip (minutes) information when user hovers on the line chart
* @function CustomTooltip
* @param { boolean } active: inital value false / becomes true when hover on linechart
* @param { array } payload: contains data to be displayed on hover
* @returns { JSX }
*/
const CustomTooltip = ({ active, payload }) => {
    if (active && payload && payload.length) {
        return (
            <div
        style={{
          background: 'white',
          fontSize: 10,
          fontWeight: 500,
          textAlign: 'center',
          padding: 10,
        }}
      >
        <p>{`${payload[0].value} min`}</p>
      </div>
        );
    }
    return null;
}

/*
* Renders Average Sessions Line Chart
* @function Average
* @param { Object } average
* @returns {JSX}
*/
const Average = ({ average }) => {
    const daysWeekTxt = (day) => {
        const days = ['L', 'M', 'M', 'J', 'V', 'S', 'D'];
        return days[day -1];
    };
    return (
        <div className="anaItem average">
      <h3>
        Dur√©e moyenne des <br />
        sessions
      </h3>
      <ResponsiveContainer width="100%" height="100%">
        <LineChart
          width={200}
          height={200}
          data={average.sessions}
          margin={{ top: 0, right: 10, bottom: 0, left: 10 }}
        >
          <Line
            type="monotone"
            dataKey="sessionLength"
            dot={false}
            activeDot={{ r: 5, strokeOpacity: 0.2, strokeWidth: 10 }}
            stroke="rgba(255, 255, 255, 0.5)"
            strokeWidth={2}
          />
          <XAxis
            dataKey="day"
            axisLine={false}
            mirror={true}
            padding={{ left: 10, right: 10 }}
            stroke="rgba(255, 255, 255, 0.5)"
            style={{ fontSize: 12, fontWeight: 500 }}
            tickLine={false}
            tickMargin={15}
            tickFormatter={daysWeekTxt}
          />
          <YAxis
            axisLine={false}
            domain={['dataMin - 20', 'dataMax + 40']}
            mirror={true}
            tickCount={0}
            tickLine={false}
          />
          <Tooltip
            content={CustomTooltip}
            cursor={{ stroke: 'black', strokeOpacity: 0.2, strokeWidth: 40 }}
            offset={30}
          />
        </LineChart>
      </ResponsiveContainer>
    </div>
    );
};

export default Average;
