import React, { useEffect, useState } from 'react';
import { Navigate, useParams } from 'react-router';

import dataMocked from '../service/data-mocked';

import Hello from '../components/Hello';
import Loader from '../components/Loader';
import Main from '../components/layout/Main';
import Aside from '../components/layout/Aside';

/**
 * @param { Integer } props id: used for route
 * @returns { JSX }
 */

const Home = () => {
    // get id from url
    const userId = useParams().id;

    // state initialisation
    const [loading, setLoading] = useState(true);
    const [activity, setActivity] = useState([]);
    const [average, setAverage] = useState([]);
    const [perfomance, setPerformance] = useState([]);
    const [keyData, setKeyData] = useState(null);

    // get data after state update
    useEffect(() => {
        const getKeyData = async () => {
            return dataMocked.getMainData(userId);
        };

        getKeyData()
        .then((user) => setKeyData(user))
        .catch((e) => {
            console.log(e);
            setLoading(false)
        })
        .finally(() => {
            setTimeout(() => {
                setLoading(false);
            }, 1500);
        });
    }, []);

    useEffect(() => {
        if (keyData) {
            const getActivity = async () => {
                return dataMocked.getUserActivity(userId);
            };

            const getAverage = async () => {
                return dataMocked.getUserAverage(userId); 
            };

            const getPerformance = async () => {
                return dataMocked.getUserPerformance(userId);
            };

            Promise.all([getActivity(), getAverage(), getPerformance()])
            .then((values) => {
                setActivity(values[0]);
                setAverage(values[1]);
                setPerformance(values[2]);
            })
            .catch((e) => {
                console.log(e);
            })
            .finally(() => {
                setTimeout(() => {
                    setLoading(false);
                }, 1500);
            });
        }
    }, [keyData]);

    // if wrong user URL
    if (loading) {
        return <Loader />
    }
    console.log(loading);
    if (keyData === undefined || keyData === null) {
        return <Navigate to="not-found" />
    }


    return (
        <div className="home">
            <Hello currentUser={keyData} />
            <div className="body-graph">
                <Main 
                    activity={activity}
                    average={average}
                    performance={performance}
                    keyData={keyData}
                />
                <Aside countData={keyData.keyData} />
            </div>
        </div>
    );
};

export default Home;
