import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router';

import dataMocked from '../service/data-mocked';

/**
 * @param { Integer } props id: used for route
 * @returns { JSX }
 */

const Home = () => {
    // get id from url
    const userId = useParams().id;

    // state initialisation
    const [loading, setLoading] = useState(true);
    const [activity, setActivity] = useState([]);
    const [average, setAverage] = useState([]);
    const [perfomance, setPerformance] = useState([]);
    const [keyData, setKeyData] = useState(null);

    // get data after state update
    useEffect(() => {
        const getKeyData = async () => {
            return dataMocked.getMainData(userId);
        };

        getKeyData()
        .then((user) => setKeyData(user))
        .catch((e) => {
            console.log(e);
            setLoading(false)
        })
        .finally(() => {
            setTimeout(() => {
                setLoading(false);
            }, 1500);
        });
    }, []);

    return (
        <div>

        </div>
    );
};

export default Home;
